import { ButtonSolid, Grid, Box, Text, Icon, CryptoCard } from 'design-system';
import React, { useEffect, useState } from 'react';
import { getCryptoData } from "../../Repositories/CryptoRepository"

export const Dashboard = () => {

    const [data, setData] = useState()
    const [busy, setBusy] = useState(true)

    useEffect(() => {

      }, []);

      const loadCryptoData = async ()  => {
        setBusy(true);
        try {
          const data = await getCryptoData();
          setData(data.json)

        } catch (e) {
          console.log("Ha ocurrido un error")
        }
        setBusy(false)
      }

    getCryptoData().then((res) => res.json())
    .then((data) => { setData(data) })

    return (<>
        <Box bg="white" pt="28px" pl="16px" pr="16px" borderTopLeftRadius="30px" borderTopRightRadius="30px">
            <Box pl="16px" pr="16px" mb="65px">
                <Box display="flex" justifyContent="left">
                    <p>Recent</p>
                </Box>
                <Grid gridTemplateColumns={["1fr", "1fr 1fr"]} gridRowGap="24px" gridColumnGap="18px">
                    <CryptoCard
                    name={data.name}
                    abreviation="BTC"
                    value="$67,908.8"
                    variant="+2.76"
                    />
                    <CryptoCard
                    name="Bitcoin"
                    abreviation="BTC"
                    value="$67,908.8"
                    variant="+2.76"
                    />
                    <CryptoCard
                    name="Bitcoin"
                    abreviation="BTC"
                    value="$67,908.8"
                    variant="+2.76"
                    />
                    <CryptoCard
                    name="Bitcoin"
                    abreviation="BTC"
                    value="$67,908.8"
                    variant="-2.76"
                    />
                </Grid>
            </Box>
            <Box pb="32px" height="64px">
                <ButtonSolid
                    onClick={() => console.log("CLICK")}
                >
                    Show all transactions
                </ButtonSolid>
            </Box>
        </Box>
      </>
    );
  };
